const Input = ({inputLabel, inputType, inputInfo, inputValue, setInputValue}) => {

  return (
    <div className="input">
      <label htmlFor={inputInfo}>{inputLabel}</label>
      <input
        className="input-field"
        type={inputType}
        name={inputInfo}
        id={inputInfo}
        value={inputValue}
        onChange={e => setInputValue(e.target.value)}
        pattern="[0-9]"
      />
    </div>
  );
};

export default Input;
