import Input from "./Input";
import { useState } from "react";

const Form = ({ title }) => {
  const [name, setName] = useState("");
  const [job, setJob] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [ipAddress, setIpAddress] = useState("");

  const clearInputs = () => {
    setName("");
    setJob("");
    setPhoneNumber("");
    setIpAddress("");
  } 

  return (
    <form className="ip-form container" onSubmit={e => e.preventDefault()}>
      <h1 className="form-title">{title}</h1>

      <Input
        inputLabel="Nome"
        inputType="text"
        inputInfo="name"
        inputValue={name}
        setInputValue={setName}
      />

      <div className="input-row">
        <Input
          inputLabel="ProfissÃ£o"
          inputType="text"
          inputInfo="job"
          inputValue={job}
          setInputValue={setJob}
        />
        <Input
          inputLabel="Celular"
          inputType="text"
          inputInfo="phone-number"
          inputValue={phoneNumber}
          setInputValue={setPhoneNumber}

        />
      </div>

      <div className="ip-field input-row">
        <Input
          inputLabel="Meu IP"
          inputType="number"
          inputInfo="ip-address"
          inputValue={ipAddress}
          setInputValue={setIpAddress}
        />
        <button className="ip-button" type="button">Encontrar IP</button>
      </div>

      <div className="input-row buttons">
        <button type="button" onClick={() => clearInputs()}>Limpar</button>
        <button type="submit">Salvar</button>
      </div>
    </form>
  );
};

export default Form;
