import { useState } from "react";

const Input = ({inputLabel, inputNumber, inputInfo, inputValue, setInputValue}) => {
  const [isInputFocused, setIsInputFocused] = useState(false);
  
  const handleInputNumber = value => {
    if (!inputNumber) {
      setInputValue(value)
      return;
    }

    if (inputNumber === "phone-number" && isInputFocused) {
      handlePhoneNumber(value);
    } else if (inputNumber === "ip-address" && isInputFocused) {
      handleIpAddress(value);
    }

  }

  const handlePhoneNumber = value => {
    console.log(value);

  }

  const handleIpAddress = value => {
    const ipNumbers = value.split("");
    const toRenderIpNumbers = value.split("");

    if (ipNumbers.length + 1 % 3 === 0 && ipNumbers.length <= 9 && ipNumbers.length !== 0) {
      toRenderIpNumbers.push(".");
    }
    console.log(ipNumbers.join(""));
    setInputValue(ipNumbers.join(""));

    return false;
  }

  return (
    <div className="input">
      <label htmlFor={inputInfo}>{inputLabel}</label>
      <input
        className="input-field"
        type="text"
        name={inputInfo}
        id={inputInfo}
        value={inputValue}
        onChange={e => handleInputNumber(e.target.value)}
        onFocus={() => setIsInputFocused(true)}
        onBlur={() => setIsInputFocused(false)}
        onKeyPress={(e) => !/[0-9]/.test(e.key) && inputNumber && e.preventDefault()}
      />
    </div>
  );
};

export default Input;
